// Add global styles and fonts here

const theme = {
    colors: {
        primary: {
            safe: '#84C1CC',
            limited: '#AA7372',
            restricted: '#6C1915',
            quarantined: '#455624',
            notification: '', // red - number of notifications
            background: '#FFFFFF',
            contactsGraph: '#C2CEAA',
        },
        fonts: {
            light: '#E2E2E2',
            medium: '#707070',
            dark: '#4D4D4D',
        },
    },
    fonts: {
        titles: 'Rockwell', // 'Rockwell'
        secondary: 'DIN Alternate', // 'DIN Alternate'
    },
  }
  
  export default theme
