// Add global styles and fonts here

const theme = {
    colors: {
        primary: {
            oldSafe: '#84C1CC',
            // limited: '#AA7372',
            // restricted: '#6C1915',
            // quarantined: '#455624',
            safe: '#A5D38D',
            limited: '#DECB5E',
            restricted: '#EFA148',
            quarantined: '#F56565',
            notification: '', // red - number of notifications
            background: '#FFFFFF',
            contactsGraph: '#C2CEAA',
        },
        fonts: {
            light: '#E2E2E2',
            medium: '#707070',
            dark: '#4D4D4D',
        },
    },
    fonts: {
        titles: 'Rockwell', // 'Rockwell' // leave empty string for windows for now
        secondary: 'DIN Alternate', // 'DIN Alternate'
    },
  }
  
  export default theme
